import React, { Component } from 'react';
import { connect } from 'react-redux';
import {
  BrowserRouter as Router,
  Route
} from 'react-router-dom';
import NavBar from '../components/NavBar'
import Home from '../components/Home';
import Matrix from '../components/Matrix.js'
import MessageList from '../MessageList';
import {fetchMessages} from '../actions/messageActions';
import GifListContainer from '../containers/GifListContainer'

class App extends Component {
  componentDidMount() {
    this.props.fetchMessages()
  }
  render() {
    return (
      <div>
        <h1>Messages:</h1>
        {(this.props.loading) ? <h4>loading messages</h4> : <h4>messagess loaded!</h4>}
        <MessageList messageDetail={this.props.messageDetail}/>
        {/* add CatList component here */}
            <Router>
            <React.Fragment>
            <NavBar />
            <Route exact path="/" component={Home} />
            <Route exact path="/drawing" component={Matrix} />
            <Route exact path="/giphy" component={GifListContainer} />
            </React.Fragment>
          </Router>
      </div>
    );
  }
}

const mapDispatchToProps = (dispatch) => ({
  fetchMessages: () => dispatch(fetchMessages())
})
const mapStateToProps = (state) => ({
  messageDetail: state.messagess,
  loading: state.loading
})


export default connect(mapStateToProps, mapDispatchToProps)(App)
