import React, { Component } from 'react';
import { connect } from 'react-redux';
import {
  BrowserRouter as Router,
  Route
} from 'react-router-dom';
import NavBar from '../components/NavBar'
import Home from '../components/Home';
import Matrix from '../components/Matrix.js'
import MessageList from '../MessageList';
import {fetchMessages} from '../actions/messageActions';
import GifListContainer from '../containers/GifListContainer'

class App extends Component {


  componentDidMount() {
     console.log(this.props)
     this.props.fetchMessages()
   }

  handleLoading = () => {
    console.log(this.props.loading)
    if(this.props.loading) {
      return <div>Loading...</div>
    } else {
      return <MessageList messageDetails={this.props.messageDetails} />
    }
  }

  render() {
    return (
      <div className="App">
        <Router>
          <React.Fragment>
          <NavBar />
          <Route exact path="/" component={Home} />
          <Route exact path="/drawing" component={Matrix} />
          <Route exact path="/giphy" component={GifListContainer} />
          </React.Fragment>
        </Router>

        <h1>Messages:</h1>
        <MessageList messageDetails={this.props.messages}/>


      </div>
    );
  }
}

const mapStateToProps = state => {
  return {
    messageDetails: state.messages,
    loading: state.loading
  }
}

const mapDispatchToProps = dispatch => {
  return {
    fetchMessages: () => dispatch(fetchMessages())
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(App)
