const messagesReducer = (state = { detail: [], loading: false }, action) => {
  switch(action.type) {
    case 'LOADING_MESSAGES':
      return {
        ...state,
        details: [...state.details],
        loading: true
      }
    case 'ADD_MESSAGE':
      return {
        ...state,
        details: action.details,
        loading: false
      }
    default:
      return state;
  }
}

export default messagesReducer;
