export const fetchMessages = ()=> {
  return (dispatch)=> {
    // 'https://learn-co-curriculum.github.io/cat-api/cats.json'
    dispatch({type: "LOADING_MESSAGES"})
    fetch('http://localhost:3000/messages').then(r=> r.json())
    .then(messages=> dispatch({type: "ADD_MESSAGES", messages: messages.details}))
  }
}
