const messagesReducer = (state = { messages: [], loading: false }, action) => {
  switch(action.type) {
    case 'LOADING_MESSAGES':
      return {
        ...state,
        messages: [...state.messages],
        loading: true
      }
    case 'ADD_MESSAGES':
      return {
        ...state,
        messages: action.messagess,
        loading: false
      }
    default:
      return state;
  }
}

export default messagesReducer;
