import MessageCard from '../components/MessageCard'
import React from 'react'
import MessageForm from '../components/MessageForm'
import {connect} from 'react-redux'
import {fetchMessages}  from '../actions/messageActions'

const url ="http://localhost:3000"

class MessagesContainer extends React.Component{

    state = {
    search: ""
    }

    addMessage = (messageData) => {
         this.setState({messages: [...this.state.messages, messageData]})
         this.setState({messages: [...this.state.messages, messageData]})
         this.setState({messages: [...this.state.messages, messageData]})
         this.setState({messages: [...this.state.messages, messageData]})
    }
    makeMessageCards(){
        let displayedMessages = this.props.messages
        console.log(this.state.search)
        if(this.state.search){
            displayedMessages = this.props.messages.filter((message) => message.detail.toLowerCase().includes(this.state.search.toLowerCase()))
    }

    return displayedMessagess.map(message => <MessageCard message={message} id={message.id} detail={message.detail} />)

    }

    componentDidMount(){
        fetch(url)
        .then(res => res.json())
        .then(json => { this.props.setToys(json) })
    }


    handleInputChange = (e) => {
        const search = e.target.value
        this.setState({search: search})
    }

      render(){
          return(
              <div id="message-container">
                  <div>
                      <MessageForm addMessage={this.addMessage}/>
                      <br></br>
                  </div>
                 {this.makeMessageCards()}
              </div>
          )
      }
  }
const mapStateToProps = (state) => {
   return {
   messages: state.messages
   }
 }
 const mapDispatchToProps = (dispatch) => {
   return {
   setMessages: () => dispatch({type: "GOT MESSAGES", payload: messages})
   }
 }
export default connect(mapStateToProps, mapDispatchToProps)(MessagesContainer)
